import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import ListView from './views/ListView';
import GalleryView from './views/GalleryView';
import DetailView from './views/DetailView';
import './App.css';

function App() {
  return (
    <BrowserRouter basename="/cs409-fa25-mp2">
      <div className="App">
        <nav>
          <ul>
            <li>
              <Link to="/">List View</Link>
            </li>
            <li>
              <Link to="/gallery">Gallery View</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<ListView />} />
          <Route path="/gallery" element={<GalleryView />} />
          <Route path="/pokemon/:id" element={<DetailView />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Pokemon, PokemonListResult } from '../types';

const ListView: React.FC = () => {
    const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortKey, setSortKey] = useState<'name' | 'id'>('id');
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

    useEffect(() => {
        const fetchPokemon = async () => {
            try {
                const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
                const results: PokemonListResult[] = response.data.results;
                const pokemonDetails = await Promise.all(
                    results.map(p => axios.get(p.url).then(res => res.data))
                );
                setPokemonList(pokemonDetails);
            } catch (error) {
                console.error('Error fetching Pokémon:', error);
            }
        };
        fetchPokemon();
    }, []);

    const filteredAndSortedPokemon = useMemo(() => {
        return pokemonList
            .filter(p => p.name.toLowerCase().includes(searchTerm.toLowerCase()))
            .sort((a, b) => {
                const aValue = a[sortKey];
                const bValue = b[sortKey];
                if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
                if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
                return 0;
            });
    }, [pokemonList, searchTerm, sortKey, sortOrder]);

    return (
        <div>
            <h2>Pokémon List</h2>
            <input
                type="text"
                placeholder="Search Pokémon"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
            />
            <div>
                Sort by:
                <select onChange={e => setSortKey(e.target.value as 'name' | 'id')} value={sortKey}>
                    <option value="id">ID</option>
                    <option value="name">Name</option>
                </select>
                <select onChange={e => setSortOrder(e.target.value as 'asc' | 'desc')} value={sortOrder}>
                    <option value="asc">Ascending</option>
                    <option value="desc">Descending</option>
                </select>
            </div>
            <ul>
                {filteredAndSortedPokemon.map(pokemon => (
                    <li key={pokemon.id}>
                        <Link to={`/pokemon/${pokemon.id}`}>
                            {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ListView;

import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Pokemon, PokemonListResult } from '../types';

const GalleryView: React.FC = () => {
    const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);
    const [selectedTypes, setSelectedTypes] = useState<string[]>([]);
    const allTypes = useMemo(() => {
        const types = new Set<string>();
        pokemonList.forEach(p => p.types.forEach(t => types.add(t.type.name)));
        return Array.from(types);
    }, [pokemonList]);

    useEffect(() => {
        const fetchPokemon = async () => {
            try {
                const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
                const results: PokemonListResult[] = response.data.results;
                const pokemonDetails = await Promise.all(
                    results.map(p => axios.get(p.url).then(res => res.data))
                );
                setPokemonList(pokemonDetails);
            } catch (error) {
                console.error('Error fetching Pokémon:', error);
            }
        };
        fetchPokemon();
    }, []);

    const handleTypeChange = (type: string) => {
        setSelectedTypes(prev =>
            prev.includes(type) ? prev.filter(t => t !== type) : [...prev, type]
        );
    };

    const filteredPokemon = useMemo(() => {
        if (selectedTypes.length === 0) {
            return pokemonList;
        }
        return pokemonList.filter(p =>
            selectedTypes.every(st => p.types.some(pt => pt.type.name === st))
        );
    }, [pokemonList, selectedTypes]);

    return (
        <div>
            <h2>Pokémon Gallery</h2>
            <div>
                {allTypes.map(type => (
                    <label key={type}>
                        <input
                            type="checkbox"
                            checked={selectedTypes.includes(type)}
                            onChange={() => handleTypeChange(type)}
                        />
                        {type.charAt(0).toUpperCase() + type.slice(1)}
                    </label>
                ))}
            </div>
            <div className="gallery">
                {filteredPokemon.map(pokemon => (
                    <Link to={`/pokemon/${pokemon.id}`} key={pokemon.id} className="gallery-item">
                        <img src={pokemon.sprites.front_default} alt={pokemon.name} />
                        <p>{pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}</p>
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default GalleryView;

