import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Pokemon, PokemonListResult } from '../types';
import { FaSearch } from 'react-icons/fa';

const ListView: React.FC = () => {
    const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortKey, setSortKey] = useState<'name' | 'id'>('id');
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');
    const [currentPage, setCurrentPage] = useState(1);
    const ITEMS_PER_PAGE = 50;

    useEffect(() => {
        const fetchPokemon = async () => {
            try {
                const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
                const results: PokemonListResult[] = response.data.results;
                const pokemonDetails = await Promise.all(
                    results.map(p => axios.get(p.url).then(res => res.data))
                );
                setPokemonList(pokemonDetails);
            } catch (error) {
                console.error('Error fetching Pokémon:', error);
            }
        };
        fetchPokemon();
    }, []);

    useEffect(() => {
        setCurrentPage(1);
    }, [searchTerm]);

    const handleSort = (key: 'name' | 'id') => {
        if (sortKey === key) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortKey(key);
            setSortOrder('asc');
        }
    };

    const filteredAndSortedPokemon = useMemo(() => {
        return pokemonList
            .filter(p => p.name.toLowerCase().includes(searchTerm.toLowerCase()))
            .sort((a, b) => {
                const aValue = a[sortKey];
                const bValue = b[sortKey];
                if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
                if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
                return 0;
            });
    }, [pokemonList, searchTerm, sortKey, sortOrder]);

    const paginatedPokemon = useMemo(() => {
        const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
        const endIndex = startIndex + ITEMS_PER_PAGE;
        return filteredAndSortedPokemon.slice(startIndex, endIndex);
    }, [filteredAndSortedPokemon, currentPage]);

    const totalPages = Math.ceil(filteredAndSortedPokemon.length / ITEMS_PER_PAGE);

    return (
        <div className="list-view">
            <span className="search-container">
                <FaSearch />
                <input
                    type="text"
                    placeholder="Search Pokémon"
                    value={searchTerm}
                    onChange={e => setSearchTerm(e.target.value)}
                />
            </span>
            <div className="list-container">
                <div className="list-header">
                    <div className="list-header-item" onClick={() => handleSort('id')}>
                        ID {sortKey === 'id' && (sortOrder === 'asc' ? '▲' : '▼')}
                    </div>
                    <div className="list-header-item" onClick={() => handleSort('name')}>
                        Name {sortKey === 'name' && (sortOrder === 'asc' ? '▲' : '▼')}
                    </div>
                </div>
                <ul className="pokemon-list">
                    {paginatedPokemon.map(pokemon => (
                        <li key={pokemon.id} className="list-item">
                            <Link to={`/pokemon/${pokemon.id}`} className="list-item-link">
                                <span className="pokemon-id">#{pokemon.id.toString().padStart(3, '0')}</span>
                                <span className="pokemon-name">{pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
                {totalPages > 1 && (
                    <div className="pagination-controls">
                        <button
                            onClick={() => setCurrentPage(p => Math.max(p - 1, 1))}
                            disabled={currentPage === 1}
                        >
                            Previous
                        </button>
                        <span>Page {currentPage} of {totalPages}</span>
                        <button
                            onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))}
                            disabled={currentPage === totalPages || totalPages === 0}
                        >
                            Next
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

.pokemon-name {
    flex: 4;
}

.pagination-controls {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 1rem;
}

.pagination-controls button {
    margin: 0 1rem;
}

